            }

            <span class="hljs-keyword">return</span> <span class="hljs-number">0</span>;
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">ThrowIfNegativeCapacity</span>(<span class="hljs-params"><span class="hljs-built_in">int</span> capacity</span>)</span>
        {
            <span class="hljs-keyword">if</span> (capacity &lt; <span class="hljs-number">0</span>)
            {
                <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> InvalidOperationException(<span class="hljs-string">&quot;Graph capacity should always be a non-negative integer.&quot;</span>);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">ThrowIfWeightZero</span>(<span class="hljs-params"><span class="hljs-built_in">double</span> weight</span>)</span>
        {
            <span class="hljs-keyword">if</span> (weight.Equals(<span class="hljs-number">0.0</span>d))
            {
                <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> InvalidOperationException(<span class="hljs-string">&quot;Edge weight cannot be zero.&quot;</span>);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">ThrowIfEdgeExists</span>(<span class="hljs-params"><span class="hljs-built_in">double</span> currentEdgeWeight</span>)</span>
        {
            <span class="hljs-keyword">if</span> (!currentEdgeWeight.Equals(<span class="hljs-number">0.0</span>d))
            {
                <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> InvalidOperationException(<span class="hljs-string">$&quot;Vertex already exists: <span class="hljs-subst">{currentEdgeWeight}</span>&quot;</span>);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">ThrowIfOverflow</span>()</span>
        {
            <span class="hljs-keyword">if</span> (Count == capacity)
            {
                <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> InvalidOperationException(<span class="hljs-string">&quot;Graph overflow.&quot;</span>);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">ThrowIfVertexNotInGraph</span>(<span class="hljs-params">Vertex&lt;T&gt; vertex</span>)</span>
        {
            <span class="hljs-keyword">if</span> (vertex.Graph != <span class="hljs-keyword">this</span>)
            {
                <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> InvalidOperationException(<span class="hljs-string">$&quot;Vertex does not belong to graph: <span class="hljs-subst">{vertex}</span>.&quot;</span>);
            }
        }
    }
}
